--
-- Structure for view `view_activity_expense_totals`
--
DROP TABLE IF EXISTS `view_activity_expense_totals`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_activity_expense_totals`  AS SELECT `ae`.`activityID` AS `activityID`, count(`ae`.`expenseID`) AS `expenseCount`, sum(`ae`.`expenseAmount`) AS `totalExpenses`, sum(case when `ae`.`reimbursable` = 'Y' then `ae`.`expenseAmount` else 0 end) AS `totalReimbursable`, sum(case when `ae`.`reimbursable` = 'N' then `ae`.`expenseAmount` else 0 end) AS `totalNonReimbursable`, sum(case when `ae`.`reimbursementStatus` = 'pending' then `ae`.`expenseAmount` else 0 end) AS `pendingReimbursement`, sum(case when `ae`.`reimbursementStatus` = 'approved' then `ae`.`expenseAmount` else 0 end) AS `approvedReimbursement`, sum(case when `ae`.`reimbursementStatus` = 'paid' then `ae`.`expenseAmount` else 0 end) AS `paidReimbursement` FROM `tija_activity_expenses` AS `ae` WHERE `ae`.`Suspended` = 'N' GROUP BY `ae`.`activityID` ;

-- --------------------------------------------------------

